# Define the MCP Server context
class MCPContext:
    def __init__(self):
        self.written = []
        self.access_key = None

    # Method to set the access key
    def set_access_key(self, key):
        self.access_key = key

    # Method to add a written resource
    def add_written(self, resource):
        self.written.append(resource)

# Define the Cedar Policy
class WriteAtMostOncePolicy:
    def evaluate(self, principal, action, resource, context):
        if action in ["write_file", "edit_file"] and context.access_key not in context.written:
            return True
        elif action not in ["write_file", "edit_file"]:
            return True
        return False

# Define a test case
def test_write_at_most_once():
    context = MCPContext()
    context.set_access_key("user1:resource1")
    context.add_written("user2:resource2")
    policy = WriteAtMostOncePolicy()
    print(policy.evaluate("user1", "write_file", "resource1", context))  # Expected: True
    print(policy.evaluate("user1", "read", "resource1", context))

